import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Recipe } from 'src/app/models/recipres.model';

@Component({
  selector: 'app-recipe-item',
  templateUrl: './recipe-item.component.html',
  styleUrls: ['./recipe-item.component.css']
})
export class RecipeItemComponent {

  @Input() recipe!:Recipe;

  @Output() recipeSelected = new EventEmitter<void>()


  onSelectRecipe(){

    this.recipeSelected.emit();

  }

}
<a href="#" class="list-group-item clearfix"  (click)="onSelectRecipe()">
  <hr>
<div class="d-flex justify-content-between">
  <div class="">

    <h4 class="list-group-item"> {{recipe.name}}</h4>
    <p class="list-group-item">{{recipe.description}}</p>
    </div>
    <div class="">
      <img [src]="recipe.imagePath" alt="" class="img-responsive" style="max-height: 70px;">
    </div>
</div>
</a>

