import { Component } from '@angular/core';
import { Recipe } from '../models/recipres.model';

@Component({
  selector: 'app-recipes',
  templateUrl: './recipes.component.html',
  styleUrls: ['./recipes.component.css']
})
export class RecipesComponent {

  selected?:Recipe;


}
<div class="row">
  <div class="col-md-5">
    <app-recipe-list  (recipeSelected) = "selected = $event"></app-recipe-list>
  </div>
  <div class="col-md-7">
    <app-recipre-detail 
    [recipe]="selected"></app-recipre-detail>
  </div>
</div>
