import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { Recipe } from 'src/app/models/recipres.model';
import { RecipeService } from 'src/app/recipe.service';

@Component({
  selector: 'app-recipre-detail',
  templateUrl: './recipre-detail.component.html',
  styleUrls: ['./recipre-detail.component.css']
})
export class RecipreDetailComponent implements OnInit {

  recipe!: Recipe;

  id!: number;


constructor(
  private route: ActivatedRoute,
   private serviceRe: RecipeService,
   private router: Router,

   ){

}

ngOnInit(): void {
  this.route.params.subscribe(
    (param:Params) => {
      this.id = + param['id'];
 this.recipe = this.serviceRe.getRecipeById(this.id);


    }
  )
}

onAddShoppingList(){

  this.router.navigate(['edit'])

}


}
<div class="row ">
  <div class="col-xs-12 mt-4">
    <img [src]="recipe.imagePath" alt="{{recipe.name}}" class="img-responsive" style="height: 300px; width: 300px;">
  </div>
</div>
<div class="row">
  <div class="col-xs-12">
  <h1>  {{recipe.name}}</h1>
  </div>
</div>
<div class="row ">
    <div class="col-xs-12">
      <div ngbDropdown class="d-inline-block">
        <button type="button" class="btn btn-primary" id="dropdownBasic1" ngbDropdownToggle>
          Toggle dropdown
        </button>
        <div ngbDropdownMenu aria-labelledby="dropdownBasic1">
          <button ngbDropdownItem (click)="onAddShoppingList()">Add to shopping List</button>
          <button ngbDropdownItem (click)="onEditShoppingList()"> Edit</button>
          <button ngbDropdownItem (click)="onDeleteShoppingList()">Delete</button>
        </div>
      </div>
    </div>

</div>
<div class="row">
  <div class="col-xs-12">
{{recipe.description}}
  </div>
<div class="row">
  <div class="col-xs-12">
ingredients
  </div>
</div>
