import { Recipe } from 'src/app/models/recipres.model';

@Component({
  selector: 'app-recipre-detail',
  templateUrl: './recipre-detail.component.html',
  styleUrls: ['./recipre-detail.component.css']
})
export class RecipreDetailComponent {

  @Input() recipe?:Recipe;

}
<div class="row">
  <div class="col-xs-12">
    <img [src]="recipe?.imagePath" alt="{{recipe?.name}}" class="img-responsive" style="max-height:300px ;">
  </div>
</div>
<div class="row">
  <div class="col-xs-12">
  <h1>  {{recipe?.name}}</h1>
  </div>
</div>
<div class="row">
  <div class="col-xs-12">

  </div>
</div>
