import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute, Params } from '@angular/router';
import { Recipe } from 'src/app/models/recipres.model';
import { RecipeService } from 'src/app/recipe.service';

@Component({
  selector: 'app-recipre-detail',
  templateUrl: './recipre-detail.component.html',
  styleUrls: ['./recipre-detail.component.css']
})
export class RecipreDetailComponent implements OnInit {

  recipes!: Recipe;

  id!: number;


constructor(private route: ActivatedRoute, private serviceRe: RecipeService){

}

ngOnInit(): void {
  this.route.params.subscribe(
    (param:Params) => {
      this.id = + param['id'];
 this.recipes = this.serviceRe.getRecipeById(this.id);


    }
  )
}


}
<div class="row">
  <div class="col-xs-12">
    <img [src]="recipe?.imagePath" alt="{{recipe?.name}}" class="img-responsive" style="max-height:300px ;">
  </div>
</div>
<div class="row">
  <div class="col-xs-12">
  <h1>  {{recipe?.name}}</h1>
  </div>
</div>
<div class="row">
    <div class="col-xs-12">
      <div ngbDropdown class="d-inline-block">
        <button type="button" class="btn btn-primary" id="dropdownBasic1" ngbDropdownToggle>
          Toggle dropdown
        </button>
        <div ngbDropdownMenu aria-labelledby="dropdownBasic1">
          <button ngbDropdownItem>Action - 1</button>
          <button ngbDropdownItem>Another Action</button>
          <button ngbDropdownItem>Something else is here</button>
        </div>
      </div>
    </div>

</div>
<div class="row">
  <div class="col-xs-12">
{{recipe?.description}}
  </div>
