import { Component, ElementRef, EventEmitter, Output, ViewChild } from '@angular/core';
import { Ingredient } from 'src/app/models/ingredient.model';

@Component({
  selector: 'app-shopping-edit',
  templateUrl: './shopping-edit.component.html',
  styleUrls: ['./shopping-edit.component.css']
})
export class ShoppingEditComponent {

  @ViewChild('inputName', {static:false})
  nameInputRef!: ElementRef;

  @ViewChild('inputAmount', { static: false })
  amountInputRef!: ElementRef;

  @Output() ingredients = new EventEmitter<Ingredient>

  onAddIngredient(ingredient:Ingredient){

    const name = this.nameInputRef.nativeElement.value;
    const amout = this.amountInputRef.nativeElement.value;

    newIngredient = new Ingredient(name,amout)

    this.ingredients.emit()

  }

}
<div class="row">
  <div class="col-xs-12">
    <form >
      <div class="row">
        <div class="col-sm-5 form-group">
          <label for="name">Name</label>
          <input type="text" name="name"
          #inputName
          class="form-control">
        </div>
        <div class="col-sm-2 from-group">
          <label for="price">Amount</label>
          <input type="number" name="price"
          #inputAmount
           id="" class="form-control">
        </div>
      </div>
      <div class="row mt-4">
        <div class="col-xs-12">
          <button class="btn btn-success" type="submit" (click)="onAddIngredient()"> Add</button>
          <button class="btn btn-danger" type="button" > Delete</button>
          <button class="btn btn-primary" type="button" > Clear</button>
        </div>
      </div>
    </form>
  </div>
</div>
