import { Component, EventEmitter, Output } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { RecipeService } from '../recipe.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  constructor(
    private route: ActivatedRoute,
     private serviceRe: RecipeService,
     private router: Router,

     ){

  }



  onAddShoppingList(){
    this.router.navigate(['edit'], {relativeTo: this.route})
  }
  onEditShoppingList(){
    this.router.navigate(['edit'], {relativeTo: this.route})
  }
  onDeleteShoppingList(){
    this.router.navigate(['edit'], {relativeTo: this.route})
  }


}
<!-- navbar.component.html -->

<nav class="navbar navbar-expand-lg bg-body-secondary ">
  <div class="container-fluid ">
    <a class="navbar-brand">MyRecipeDay</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNavDropdown">
      <ul class="navbar-nav">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" routerLinkActive="link-active" routerLink="/recipes">Recipes</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLinkActive="link-active" routerLink="/shopping-list">Shopping List</a>
        </li>
      </ul>

    </div>
    <div class="row ">
      <div class="col-xs-12">
        <div ngbDropdown class="d-inline-block">
          <button type="button" class="btn btn-primary" id="dropdownBasic1" ngbDropdownToggle>
            Toggle dropdown
          </button>
          <div ngbDropdownMenu aria-labelledby="dropdownBasic1">
            <button ngbDropdownItem (click)="onAddShoppingList()">Add to shopping List</button>
            <button ngbDropdownItem (click)="onEditShoppingList()"> Edit</button>
            <button ngbDropdownItem (click)="onDeleteShoppingList()">Delete</button>
          </div>
        </div>
      </div>


  </div>

  </div>


</nav>
