import { Injectable } from '@angular/core';
import { Recipe } from './models/recipres.model';

@Injectable({
  providedIn: 'root'
})
export class RecipeService {

  constructor() { }



   recipes : Recipe[] = [
      new Recipe('a test recipe','04102023-ratatouille-lede','https://assets.bonappetit.com/photos/64349ba03fd52da4745a35f4/1:1/w_2240,c_limit/04102023-ratatouille-lede.jpg',[]),
      new Recipe('the best recipe','tMary-Berry-Tuscan-Chicken-Recipe-Cook-and-Share-BBC2','https://thehappyfoodie.co.uk/wp-content/uploads/2022/08/Mary-Berry-Tuscan-Chicken-Recipe-Cook-and-Share-BBC2.jpg'),
      new Recipe('easy-dinner-ideas-1','this is a test','https://cdn.loveandlemons.com/wp-content/uploads/2019/09/easy-dinner-ideas-1.jpg')
    ];



  getRecipeById(index:number){
    return this.recipes[index];
  }
}
