import { Component, EventEmitter, Output } from '@angular/core';
import { Recipe } from 'src/app/models/recipres.model';

@Component({
  selector: 'app-recipe-list',
  templateUrl: './recipe-list.component.html',
  styleUrls: ['./recipe-list.component.css']
})
export class RecipeListComponent {

  @Output() recipeWasSelected = new EventEmitter<Recipe>();


  recipes : Recipe[] = [
    new Recipe('a test recipe','this is a test','https://assets.bonappetit.com/photos/64349ba03fd52da4745a35f4/1:1/w_2240,c_limit/04102023-ratatouille-lede.jpg'),
    new Recipe('a test recipe','this is a test','https://assets.bonappetit.com/photos/64349ba03fd52da4745a35f4/1:1/w_2240,c_limit/04102023-ratatouille-lede.jpg'),
    new Recipe('a test recipe','this is a test','https://assets.bonappetit.com/photos/64349ba03fd52da4745a35f4/1:1/w_2240,c_limit/04102023-ratatouille-lede.jpg')
  ];

  onRecipeSelected(recipe:Recipe)
  {
    console.log(recipe);
    this.recipeWasSelected.emit(recipe);

  }

}
<div class="row mt-4">
  <div class="col-xs-12">
    <button class="btn btn-success">New recipe</button>
  </div>
  <div class="row">
    <div class="col-xs-12" >
      <app-recipe-item
      *ngFor="let recipeEl of recipes"
      (recipeSelected)="onSelectRecipe(recipeEl)"
       [recipe]="recipeEl"></app-recipe-item>
    </div>
  </div>
</div>




<div class="row">
  <div class="col-xs-12">
    <button class="btn btn-success">New Recipe</button>
  </div>
</div>
<hr>
<div class="row">
  <div class="col-xs-12">
    <app-recipe-item
      *ngFor="let recipeEl of recipes"
      [recipe]="recipeEl"
      (recipeSelected)="onRecipeSelected(recipeEl)"></app-recipe-item>
  </div>
</div>
